import React from 'react'
import {Avatar, FormControlLabel, Grid, Paper, TextField, Switch, Button, withStyles} from '@material-ui/core'
import VpnKeyIcon from '@material-ui/icons/VpnKey';
import { purple } from '@material-ui/core/colors';
const SignIn=()=>{
    const paperStyle={padding :20, height:'70vh', width:280, margin:'20px auto'}
    const avatarStyle={backgroundColor:'#E3E2B4',margin:'10px'}
    const textfieldStyle={margin:'10px auto'}
    const pinkSwitch = withStyles({
        switchBase: {
          color: purple[300],
          '&$checked': {
            color: '#FFFFFF',
          },
          '&$checked + $track': {
            backgroundColor: '#FFFFFF',
          },
        },
        checked: {},
        track: {},
      })(Switch);
    return(
        <Grid>
            <Paper elevation={5} style={paperStyle}>
                <Grid align='center'>
                    <Avatar style={avatarStyle} variant='rounded'><VpnKeyIcon/></Avatar>
                    <h2>Sign in</h2>
                </Grid>
                <TextField style={textfieldStyle} id="outlined-basic" variant="outlined" label='Username' placeholder='Enter username' align='center' size='small' fullWidth required/>
                <TextField style={textfieldStyle} id="outlined-basic" variant="outlined" label='Password' placeholder='Enter password' align='center' size='small' type='password' fullWidth required/>
                <Grid>
                <FormControlLabel
                    control={<Switch pinkSwitch name="switchA" />}
                    label="Remember me"
                />
                </Grid>
                <Button type="submit"  variant='contained' style={{backgroundColor:'#BFC8D7'}} fullWidth>Sign in</Button>
            </Paper>
        </Grid>
    )
}
export default SignIn
